import React, {useState } from "react";
import Header  from "./components/Header";
import Tasks from "./components/Tasks";

function App() {
  const [tasks, setTasks] = useState([
    {
      id:1,
      text:'Print this again',
      day:'Feb 5th @2:30 pm',
      reminder: true,
    },{
      id:2,
      text:'Print that',
      day:'March 9th @7:30 pm',
      reminder: false,
    },
    {
      id:3,
      text:'And then Print this',
      day:'December 1st @1:30 am',
      remonder: true,
    }
  ])

  // Delete task
  const deleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id))
  }

  return (
    <div className="container">
     <Header/> 
     {tasks.length > 0 ? (
       <Tasks tasks={tasks} onDelete={deleteTask}/>
       ) : ('No Tasks To Show')
     }
    </div>
  );
}

export default App;
