import React from 'react';
import { useState } from 'react';

const AddTask = () => {

    const [text,setText] = useState('')
    const [day, setDay] = useState('')
    const [reminder, setReminder] = useState(false)

  return (
    <form className='add-form'>
        <div className='form-control'>
            <label>Task</label>
            <input type='text' placeholder='Add Task' value={text} onChange={(e) => setText(e.target.value)}></input>
        </div>
        <div className='form-control'>
            <label>Day & Time</label>
            <input type='text' placeholder='Add Day & Time' value={day} onChange={(e) => setDay(e.target.value)}></input>
        </div>
        <div className='form-control  form-control-check' value={reminder} onChange={(e) => setReminder(e.currentTarget.checked)}>
            <label>Set Reminder</label>
            <input type='checkbox'/>
        </div>
        <input type='submit' value='Save Task' className='btn bt-block'></input>
    </form>
  )
}

export default AddTask
